$(document).ajaxComplete(function (event, xhr, settings, e) {
    if (settings.type === "POST" && xhr.status === 200 && (settings.url.includes("Update") || settings.url.includes("Create") || settings.url.includes("Destroy"))) {
        toastr.info("İşlem Başarıyla Yapıldı.");
    }
    if (xhr.responseText.includes("Exception:")) {
        toastr.error(xhr.responseText.replace("Exception:", ""));
    }
});