insert into Resources (Name, Value, LanguageId) values ('Pages.About.Content', '<div class="container">      <div class="five columns">          <h3>Zum Unternehmen</h3>          Wir bieten Arbeitgebern und Arbeitnehmern kompetente Personaldienstleistungen aus einer Hand.<br>          Unser eingespieltes Team sorgt für eine persönliche Kundenberatung vor Ort und eine verlässliche Partnerschaft.<br><br>          Erhalten Sie in unserem Unternehmensprofil noch weitere Informationen über TBBProject!<br><br>          <a href="/pages/company-profile" class="button">Unternehmen</a>      </div>        <div class="five columns">          <h3>Zur Teamseite</h3>          Wir bringen Unternehmen und Bewerber zusammen: TBBProject besteht aus einem jungen und motivierten Team, das Ihnen jeden Tag aufs Neue mit fachlichem Know-how zur Verfügung steht.<br><br>          Wir stellen uns vor: Erfahren Sie auf unseren Filialseiten mehr über das Team von TBBProject!<br><br>          <a href="/pages/contact" class="button">Zur Teamübersicht</a>      </div>        <div class="five columns">          <h3>Ihre Karriere bei TBBProject</h3>          Sie möchten bei einem jungen, dynamischen und innovativen Unternehmen arbeiten? Dann bewerben Sie sich bei TBBProject – Ihre private Arbeitsvermittlung in Berlin.<br><br>          Egal ob festangestellte Vollzeitkraft, freiberufliche und selbstständige Tätigkeit, als Praktikant oder einer Stelle als studentische Hilfskraft: Wir suchen immer wieder nach qualifiziertem Personal.<br><br>          Klicken Sie sich durch unsere Stellenangebote – vielleicht ist Ihr Wunschjob bei TBBProject dabei!<br><br>          <a href="/category/intern-TBBProject/16/" class="button">Stellenangebote</a>      </div>  </div>', 5);
